package study.string.LongestSubstringwithAtLeastKRepeatingCharacters

import org.junit.Assert.assertEquals
import org.junit.Test

class SolutionTest {

    @Test
    fun longestSubstring() {
        assertEquals(3, Solution().longestSubstring("aaabb", 3))
        assertEquals(5, Solution().longestSubstring("ababbc", 2))
    }

    @Test
    fun test() {
        val str = ""
        val res = Solution().longestSubstring(str, 301)
        println(res)
    }
}